import {
    GET_POSTS,
    POST_ERROR,
    ADD_POST
} from '../actions/types'


const initialState = {
    posts:[],
    post :null,
    loading:true,
    error:{}
}


const  postReducer =(state=initialState,action)=>{
    const { type,payload} = action;

    switch(type){
        case GET_POSTS:
            return{
                ...state,
                posts:payload,
                loading:false
            };
            case ADD_POST:{
                return{
                    ...state,
                    posts:[...state.posts,payload],
                    loading:false
                }
            }
            case POST_ERROR:
                return{
                    ...state,
                    error:payload,
                    loading:false
                }
                default:
                    return state;
    }
}
export default postReducer;
